import 'bootstrap/dist/css/bootstrap.min.css'
import "../Styles/MainStyle.css"
import "../Styles/form.css"
import React, { useEffect} from 'react'
import {useNavigate} from 'react-router-dom'
import { useRef } from 'react'
import FormContent from './formContent'

function Form() {
    
    const navigate = useNavigate();
  
    const closeForm = () =>{
      navigate("/");
    }

    function onClose(e)
  {
    e.preventDefault();
    e.stopPropagation();

    CloseAnimation();
  }

    function CloseAnimation()
    {
      const form = document.querySelector('.form');
      let opacity = 1;
      let width = 300;
      let height = 200;

      function animate() {
          opacity -= 0.01;
          width -= 1;
          height -= 1;

          form.style.opacity = opacity;
          form.style.width = width + 'px';
          form.style.height = height + 'px';

          if (opacity > 0) {
              requestAnimationFrame(animate);
          } else {
              closeForm();
          }
      }

      animate();
    }

    function OpenAnimation()
    {
        const form = document.querySelector('.form');
        let opacity = 0;
        let width = 0;
        let height = 0;

        function animate() {
            opacity += 0.01;
            width += 1;
            height += 1;

            form.style.opacity = opacity;
            form.style.width = width + 'px';
            form.style.height = height + 'px';

            if (opacity < 1) {
                requestAnimationFrame(animate);
            }
        }

        animate();
    }

  return (
  <div class="form">
    <div class="form-wrapper">
        <button type="button" onClick={(e) => onClose(e)} id="close-btn" class="btn-close-form">
            <svg xmlns="http://www.w3.org/2000/svg" x="0px" y="0px" width="40" height="40" viewBox="0 0 48 48">
                <path fill="#F44336" d="M21.5 4.5H26.501V43.5H21.5z" transform="rotate(45.001 24 24)"></path><path fill="#F44336" d="M21.5 4.5H26.5V43.501H21.5z" transform="rotate(135.008 24 24)"></path>
            </svg>
        </button>
        <FormContent/>
    </div>
</div>
  );
}

export default Form;
