import 'bootstrap/dist/css/bootstrap.min.css'
import "../Styles/MainStyle.css"
import "../Styles/form.css"
import React, { useEffect} from 'react'
import {useNavigate} from 'react-router-dom'
import { useRef } from 'react'
import FormContent from './formContent'
import { useState } from 'react'

function Form() {
    
    const navigate = useNavigate();
    const form = useRef();
    const [isOpen, setIsOpen] = useState(false);
    const [popupWidth, setPopupWidth] = useState(0);
    const [popupHeight, setPopupHeight] = useState(0);
  
    const closeForm = () =>{
      navigate("/");
    }

    useEffect(() => {
      OpenAnimation();
    }, []);

    function onClose(e)
    {
      e.preventDefault();
      e.stopPropagation();

      // После добавления анимации обязательно заменить данную строку вызовом функции анимации закрытия (CloseAnimation),
      // в которой после окончания анимации уже будет вызвана closeForm().
      CloseAnimation();
    }

    function CloseAnimation() {
      const duration = 300;
      const initialWidth = form.current.offsetWidth;
      const initialHeight = form.current.offsetHeight;
      const targetWidth = 0;
      const targetHeight = 0;
    
      let startAnimationTime = null;
      requestAnimationFrame(function animate(time) {
        if (!startAnimationTime) startAnimationTime = time;
    
        const progress = (time - startAnimationTime) / duration;
    
        const currentWidth = initialWidth - (initialWidth - targetWidth) * progress;
        const currentHeight = initialHeight - (initialHeight - targetHeight) * progress;
    
        form.current.style.opacity = 1 - progress;
        form.current.style.width = currentWidth + "px";
        form.current.style.height = currentHeight + "px";
    
        if (progress < 1) requestAnimationFrame(animate);
        else closeForm();
      });
    }
    
    function OpenAnimation() {
      const duration = 300;
      const initialWidth = form.current.offsetWidth;
      const initialHeight = form.current.offsetHeight;
      const targetWidth = popupWidth;
      const targetHeight = popupHeight;
    
      let startAnimationTime = null;
      requestAnimationFrame(function animate(time) {
        if (!startAnimationTime) startAnimationTime = time;
    
        const progress = (time - startAnimationTime) / duration;
    
        const currentWidth = (targetWidth - initialWidth) * progress + initialWidth;
        const currentHeight = (targetHeight - initialHeight) * progress + initialHeight;
    
        form.current.style.opacity = progress;
        form.current.style.width = currentWidth + "px";
        form.current.style.height = currentHeight + "px";
    
        if (progress < 1) requestAnimationFrame(animate);
      });
    }

  return (
  <div class="form" >
    <div class="form-wrapper" ref={form}>
        <button type="button" onClick={(e) => onClose(e)} id="close-btn" class="btn-close-form">
            <svg xmlns="http://www.w3.org/2000/svg" x="0px" y="0px" width="40" height="40" viewBox="0 0 48 48">
                <path fill="#F44336" d="M21.5 4.5H26.501V43.5H21.5z" transform="rotate(45.001 24 24)"></path><path fill="#F44336" d="M21.5 4.5H26.5V43.501H21.5z" transform="rotate(135.008 24 24)"></path>
            </svg>
        </button>
        <FormContent/>
    </div>
  </div>
  );
}

export default Form;
