import 'bootstrap/dist/css/bootstrap.min.css'
import "../Styles/MainStyle.css"
import "../Styles/form.css"
import React, { useEffect} from 'react'
import {useNavigate} from 'react-router-dom'
import { useRef } from 'react'
import FormContent from './formContent'

function Form() {
  const navigate = useNavigate();
  const formRef = useRef();

  const openForm = () => {
    formRef.current.classList.add('open');
  };

  const closeForm = () => {
    formRef.current.classList.remove('open');
    navigate("/");
  };

  function OpenAnimation() {
    const form = formRef.current;
    let width = 0;
    let height = 0;
    let opacity = 0;

    function animate() {
      width += 10;
      height += 6;
      opacity += 0.05;

      form.style.width = width + 'px';
      form.style.height = height + 'px';
      form.style.opacity = opacity;

      if (width < 300) {
        requestAnimationFrame(animate);
      }
    }

    animate();
  }

  function CloseAnimation() {
    const form = formRef.current;
    let width = 300;
    let height = 200;
    let opacity = 1;

    function animate() {
      width -= 10;
      height -= 6;
      opacity -= 0.05;

      form.style.width = width + 'px';
      form.style.height = height + 'px';
      form.style.opacity = opacity;

      if (width > 0) {
        requestAnimationFrame(animate);
      } else {
        closeForm();
      }
    }

    animate();
  }

  return (
    <div ref={formRef} className="form">
      <div className="form-wrapper">
        <button type="button" onClick={CloseAnimation} id="close-btn" className="btn-close-form">
          <svg xmlns="http://www.w3.org/2000/svg" x="0px" y="0px" width="40" height="40" viewBox="0 0 48 48">
            <path fill="#F44336" d="M21.5 4.5H26.501V43.5H21.5z" transform="rotate(45.001 24 24)"></path>
            <path fill="#F44336" d="M21.5 4.5H26.5V43.501H21.5z" transform="rotate(135.008 24 24)"></path>
          </svg>
        </button>
        <FormContent />
      </div>
    </div>
  );
}

export default Form;
