import 'bootstrap/dist/css/bootstrap.min.css';
import "../Styles/MainStyle.css";
import "../Styles/faq.css";
import React, { useState } from 'react';

function FAQ() {
    const faqData = [
        {
            id: 1,
            title: "Кто непосредственно занимается поддержкой?",
            content: "Сайты поддерживают штатные сотрудники ООО «Инитлаб», г. Краснодар, прошедшие специальное обучение и имеющие опыт работы с Друпал от 4 до 15 лет: 8 web-разработчиков, 2 специалиста по SEO, 4 системных администратора."
        },
        {
            id: 2,
            title: "Можно ли пообщаться со специалистом голосом или в мессенджере?",
            content: "Для этого нужно написать заявку на консультацию на email поддержки и сообщить удобное время и контактные данные."
        }
    ];

    const [activeItem, setActiveItem] = useState(null);

    const handleItemClick = (id) => {
        setActiveItem(activeItem === id ? null : id);
    };

    return (
        <div className="block-faq">
            <div className="container">
                <div className="block-name-faq">
                    FAQ
                </div>
                <div className="question">
                    {faqData.map((faq) => (
                        <div className="question-item" key={faq.id}>
                            <div className="question-header" onClick={() => handleItemClick(faq.id)}>
                                <div className="question-title block-title">
                                    {faq.title}
                                </div>
                                <button className="question-toggle"></button>
                            </div>
                            {activeItem === faq.id && (
                                <div className="question-content">
                                    <p>{faq.content}</p>
                                </div>
                            )}
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
}

export default FAQ;
