import 'bootstrap/dist/css/bootstrap.min.css'
import "../Styles/MainStyle.css"
import "../Styles/form.css"
import Loading from './loading'
import React, { useEffect, useState } from 'react'

function FormContent() {

    const [fio, setFIO] = useState('')
    const [email, setEmail] = useState('')
    const [tel, setTel] = useState('')
    const [comment, setComment] = useState('')
    const [error, setError] = useState('')

    useEffect(() => {
        setInputValues();
    }, []);

    function ClearInputs()
    {
        setFIO('');
        setEmail('');
        setTel('');
        setComment('');
    }

    function onSubmit(e){

        e.preventDefault();
        e.stopPropagation();

        fetch('https://formcarry.com/s/fCsjmrtmZ4', {
        method: 'POST',
        headers: {'Content-Type': 'application/json', 'Accept': 'application/json'},
        body: localStorage.user
        })
        .then(response => {
            console.log(response);
            localStorage.clear();
            ClearInputs();})
        .catch(error => {
            console.log(error);
            localStorage.clear();
            ClearInputs();});
    } 

    function setInputValues()
    {
        if(localStorage.user != null)
        {
            let data = JSON.parse(localStorage.user);
            setFIO(data.fio);
            setEmail(data.email);
            setTel(data.tel);
            setComment(data.comment);
        }
    }

    function safeToLocalStorage()
    {
        console.log({fio, tel, email, comment});
        localStorage.user = JSON.stringify({fio, tel, email, comment});
    }

  return (
    <div>
    <Loading/>
        
    </div>
  );
}

export default FormContent;
