import 'bootstrap/dist/css/bootstrap.min.css'
import "../Styles/MainStyle.css"
import "../Styles/form.css"
import React, { useEffect} from 'react'
import {useNavigate} from 'react-router-dom'
import { useRef } from 'react'
import FormContent from './formContent'

function Form() {
    
    const navigate = useNavigate();
    const form = useRef();
  
    const closeForm = () =>{
      navigate("/");
    }

    useEffect(() => {
      OpenAnimation();
    }, []);

    function onClose(e)
    {
      e.preventDefault();
      e.stopPropagation();

      // После добавления анимации обязательно заменить данную строку вызовом функции анимации закрытия (CloseAnimation),
      // в которой после окончания анимации уже будет вызвана closeForm().
      CloseAnimation();
    }

    function OpenAnimation() {
      var startWidth = 0;
      var startHeight = 0;
      var endWidth = window.innerWidth;
      var endHeight = window.innerHeight;
      var startTime = null;
      var startPosition = form.current.getBoundingClientRect(); // Получаем начальную позицию формы
  
      function animate(currentTime) {
          if (!startTime) startTime = currentTime;
          var progress = currentTime - startTime;
          var newWidth = easeInOutQuad(progress, startWidth, endWidth - startWidth, 500);
          var newHeight = easeInOutQuad(progress, startHeight, endHeight - startHeight, 500);
          form.current.style.width = newWidth + 'px';
          form.current.style.height = newHeight + 'px';
          form.current.style.left = startPosition.left + 'px'; // Устанавливаем начальную позицию формы
          form.current.style.top = startPosition.top + 'px';
          if (progress < 500) {
              requestAnimationFrame(animate);
          }
      }
  
      requestAnimationFrame(animate);
  }
  
  function CloseAnimation() {
      var startWidth = window.innerWidth;
      var startHeight = window.innerHeight;
      var endWidth = 0;
      var endHeight = 0;
      var startTime = null;
      var startPosition = form.current.getBoundingClientRect();
  
      function animate(currentTime) {
          if (!startTime) startTime = currentTime;
          var progress = currentTime - startTime;
          var newWidth = easeInOutQuad(progress, startWidth, endWidth - startWidth, 500);
          var newHeight = easeInOutQuad(progress, startHeight, endHeight - startHeight, 500);
          form.current.style.width = newWidth + 'px';
          form.current.style.height = newHeight + 'px';
          form.current.style.left = startPosition.left + 'px';
          form.current.style.top = startPosition.top + 'px';
          if (progress < 500) {
              requestAnimationFrame(animate);
          } else {
              closeForm();
          }
      }
  
      requestAnimationFrame(animate);
  }
  
  // Функция easeInOutQuad для плавного изменения размеров
  function easeInOutQuad(t, b, c, d) {
      t /= d / 2;
      if (t < 1) return c / 2 * t * t + b;
      t--;
      return -c / 2 * (t * (t - 2) - 1) + b;
  }

  return (
  <div class="form" ref={form}>
    <div class="form-wrapper" >
        <button type="button" onClick={(e) => onClose(e)} id="close-btn" class="btn-close-form">
            <svg xmlns="http://www.w3.org/2000/svg" x="0px" y="0px" width="40" height="40" viewBox="0 0 48 48">
                <path fill="#F44336" d="M21.5 4.5H26.501V43.5H21.5z" transform="rotate(45.001 24 24)"></path><path fill="#F44336" d="M21.5 4.5H26.5V43.501H21.5z" transform="rotate(135.008 24 24)"></path>
            </svg>
        </button>
        <FormContent/>
    </div>
  </div>
  );
}

export default Form;
