import 'bootstrap/dist/css/bootstrap.min.css'
import "../Styles/MainStyle.css"
import "../Styles/form.css"
import React, { useEffect, useState } from 'react'
import {useNavigate} from 'react-router-dom'
import { useRef } from 'react'
import FormContent from './formContent'

function Form() {
    
    const navigate = useNavigate();
    const formItem = useRef();

    useEffect(() => {
      OpenAnimation();
  }, []);
  
    const closeForm = () =>{
      navigate("/");
    }

    function onClose(e)
    {
      e.preventDefault();
      e.stopPropagation();
      CloseAnimation();
      
      
    }

    function OpenAnimation()
    {
        let startAnimation = null;
        let duration = 1000;
        requestAnimationFrame(function measure(time)
        {
            if(!startAnimation)
              startAnimation = time;

            const progress = (time-startAnimation)/duration;
            const opacityProg = progress;

            formItem.current.style.opacity = opacityProg;

            if(progress < 1)
              requestAnimationFrame(measure)
            else closeForm();
        })
    }

    function CloseAnimation()
    {
      let startAnimation = null;
        let duration = 1000;
        requestAnimationFrame(function measure(time)
        {
            if(!startAnimation)
              startAnimation = time;

            const progress = (time-startAnimation)/duration;
            const opacityProg = progress;

            formItem.current.style.opacity = 1-opacityProg;

            if(progress < 1)
              requestAnimationFrame(measure)
        })
    }

  return (
  <div class="form" ref={formItem}>
    <div class="form-wrapper">
        <button type="button" onClick={(e) => onClose(e)} id="close-btn" class="btn-close-form">
            <svg xmlns="http://www.w3.org/2000/svg" x="0px" y="0px" width="40" height="40" viewBox="0 0 48 48">
                <path fill="#F44336" d="M21.5 4.5H26.501V43.5H21.5z" transform="rotate(45.001 24 24)"></path><path fill="#F44336" d="M21.5 4.5H26.5V43.501H21.5z" transform="rotate(135.008 24 24)"></path>
            </svg>
        </button>
        <FormContent/>
    </div>
</div>
  );
}

export default Form;
